const React = require('react');
const PropTypes = require('prop-types');
const GuestWelcome = require('./welcome');
const UserLinksSections = require('../user-links-sections');

const MobileGuestUserMenu = ({ welcome, userLinksSections, linkTags }) => (
  <>
    <GuestWelcome {...welcome} />
    <UserLinksSections
      {...userLinksSections}
      linkTags={linkTags}
    />
  </>
);

MobileGuestUserMenu.propTypes = {
  welcome: PropTypes.object.isRequired,
  userLinksSections: PropTypes.object.isRequired,
};

module.exports = MobileGuestUserMenu;
