const { getValue } = require('pi-navigation-config');
const userLinksSectionsProps = require('../user-links-sections/props');

const guestUserMenuProps = (props) => {
  const { req, platform, navigationUrls: urls } = props;
  const userMenuGuest = getValue({
    key: 'userMenuGuest',
    req,
    platform,
  });

  return {
    welcome: {
      ...userMenuGuest,
      login: urls.login,
      registration: urls.registration,
    },
    userLinksSections: userLinksSectionsProps(props),
  };
};

module.exports = guestUserMenuProps;

