/* eslint-disable react/forbid-prop-types */
const React = require('react');
const PropTypes = require('prop-types');
const Welcome = require('./welcome');
const UserLinksSections = require('../user-links-sections');

const MobileLoggedInUserMenu = (props) => {
  const { userLinksSections, welcome, linkTags } = props;

  return (
    <>
      <Welcome {...welcome} />
      <UserLinksSections {...userLinksSections} linkTags={linkTags} />
    </>
  );
};

MobileLoggedInUserMenu.propTypes = {
  userLinksSections: PropTypes.object.isRequired,
  welcome: PropTypes.object.isRequired,
  linkTags: PropTypes.object.isRequired,
};

module.exports = MobileLoggedInUserMenu;
