const loyaltyProps = (props) => {
  const { userInfo, urls } = props;
  return {
    href: urls.loyalty.link,
    level: userInfo.loyal.level,
    percentage: userInfo.loyal.percentage,
    levelName: userInfo.loyal.full_level_name,
    drawer: userInfo.loyal.drawer,
  };
};

module.exports = loyaltyProps;
