/* eslint-disable security/detect-object-injection */
const React = require('react');
const PropTypes = require('prop-types');
const HeaderHidden = require('./hidden');
const HeaderInternal = require('./internal');
const HeaderPlus = require('./plus');
const HeaderLite = require('./lite');
const { types } = require('../../layout-type-detection');

const defaultHeader = HeaderPlus;
const availableHeaders = {
  [types.INTERNAL]: HeaderInternal,
  [types.PLUS]: HeaderPlus,
  [types.LITE]: HeaderLite,
  [types.HIDDEN]: HeaderHidden,
};

const Header = (props) => {
  const { type } = props;

  const ApplicableHeader = availableHeaders[type] || defaultHeader;

  return <ApplicableHeader {...props} />;
};

Header.propTypes = {
  type: PropTypes.string.isRequired,
};

module.exports = Header;
