/* eslint-disable arrow-body-style */
const React = require('react');
const PropTypes = require('prop-types');
const Header = require('../components/base-header');
const UserLinks = require('../components/user-menu/desktop/user-links');

const HeaderLite = (props) => {
  const { lite } = props;
  const { isDesktop } = lite;

  return (
    <Header
      {...props}
      showLogo
    >
      <div className="nav-header-menu-wrapper">
        <nav id="nav-header-menu">
          { isDesktop ? <UserLinks {...lite.userLinks} /> : null }
        </nav>
      </div>
    </Header>
  );
};


HeaderLite.propTypes = {
  lite: PropTypes.shape({
    isDesktop: PropTypes.bool.isRequired,
    userLinks: PropTypes.object.isRequired,
  }).isRequired,
};


module.exports = HeaderLite;
