const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const HeaderLite = require('.');

const baseProps = {
  type: 'plus',
  siteId: 'MLA',
  logo: {
    url: 'https://www.mercadolibre.com.ar',
    label: 'Mercado Libre',
  },
  melilab: {},
  userMenu: {
    isUserLoggedIn: false,
  },
  navMenuSwitchId: 'navMenuSwitchId',
};

describe('Portal Inmobiliario Navigation', () => {
  describe('Header Lite', () => {
    it('returns the header in lite mode, web mobile', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <HeaderLite
          {...baseProps}
          lite={{
            userLinks: {},
            isDesktop: false,
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('returns the header in lite mode, desktop', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <HeaderLite
          {...baseProps}
          lite={{
            userLinks: {
              links: [],
            },
            isDesktop: true,
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
