const { mapLinksToUrls } = require('../../utils/links');

const help = 'helpMain';
const userMenuLinks = [help];

const liteHeaderProps = (props) => {
  const { urls, actualLocation } = props;

  const links = mapLinksToUrls({
    links: userMenuLinks,
    urls,
    currentUrl: actualLocation,
    originalProps: props,
    hash: 'nav-header',
    className: (link) => {
      switch (link) {
        case help:
          return 'option-help';
        default:
          return `option-${link}`;
      }
    },
  });

  return {
    userLinks: {
      links,
    },
    isDesktop: props.device.desktop || false,
  };
};

module.exports = liteHeaderProps;
