const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Header = require('.');

const baseProps = {
  type: 'plus',
  siteId: 'MLA',
  logo: {
    url: 'https://www.mercadolibre.com.ar',
    label: 'Mercado Libre',
  },
  melilab: {},
  userMenu: {
    isUserLoggedIn: false,
  },
  navMenuSwitchId: 'navMenuSwitchId',
};

describe('Portal Inmobiliario Navigation', () => {
  describe('Plus Component', () => {
    it('matches the default snapshot for plus header', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Header
          {...baseProps}
          type="plus"
          siteId="MLA"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for plus header in desktop', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Header
          {...baseProps}
          type="plus"
          siteId="MLA"
          isMobile={false}
          isDesktop
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
