const fs = require('fs');
const { join } = require('path');

const basePath = __dirname.endsWith('/lib')
  ? join(__dirname, 'client')
  : join(__dirname, '../../../lib/client');

// eslint-disable-next-line security/detect-non-literal-fs-filename
const readClientFile = script => fs.readFileSync(join(basePath, script)).toString();

module.exports = readClientFile;
