/**
 * Set the Cache Control header.
 * If the purpose header exists do not set the header instead.
 */
module.exports = function setCacheControlHeader(req, res, next) {
  if (req.headers && req.headers.purpose !== 'prefetch' && req.headers.purpose !== 'etag') {
    res.header('Cache-Control', 'private, max-age=0, no-cache, no-store, must-revalidate');
  }
  next();
};
