class PageMiddlewareError extends Error {
  constructor(functionName, error) {
    const message = `An error occurred inside ${functionName} that does not allow its execution.\n`;

    super(`${message} Error: ${error.message}`);

    this.stack = message + error.stack;
    this.name = 'PageMiddlewareError';
  }
}

module.exports = PageMiddlewareError;
