const { getServerSidePropsMiddleware, setPageSettingsMiddleware } = require('./shared-middleware');
const { sendErrorMetric } = require('../metrics');

const http404ErrorMiddleware = (req, res, next) => {
  const { statusCode, pageResources } = req.nordicPages;
  const { view } = pageResources;

  getServerSidePropsMiddleware(req, res, next)
    .then(() => {
      setPageSettingsMiddleware(req, res, next)
        .then(() => {
          next();
        })
        .catch((error) => {
          sendErrorMetric({ func: 'SET_PAGE_SETTINGS', statusCode, view });
          next(error);
        });
    })
    .catch((error) => {
      sendErrorMetric({ func: 'GET_SERVER_SIDE_PROPS', statusCode, view });
      next(error);
    });
};

module.exports = { http404ErrorMiddleware };
