const getClientSrc = require('../get-client-src');

describe('Get Client src', () => {
  it('returns the client src for a given path and script name', () => {
    const name = 'tracking.js';
    const path = '/modules/navigation';

    expect(getClientSrc(name, path)).toEqual('/modules/navigation/lib/client/tracking.js');
  });

  it('returns falsy since script name is falsy', () => {
    const name = null;
    const path = '/modules/navigation';

    expect(getClientSrc(name, path)).toBeFalsy();
  });

  it('returns falsy since path name is falsy', () => {
    const name = 'tracking.js';
    const path = null;

    expect(getClientSrc(name, path)).toBeFalsy();
  });
});
