const isUserLoggedIn = require('../is-user-logged-in');

describe('Mercado Libre Navigation', () => {
  describe('Is User Logged In', () => {
    it('returns true since there is a user', () => {
      const props = {
        user: {},
      };

      expect(isUserLoggedIn(props)).toEqual(true);
    });

    it('returns false since there is no user', () => {
      const props = {};

      expect(isUserLoggedIn(props)).toEqual(false);
    });

    it('returns false since the user is null', () => {
      const props = {
        user: null,
      };

      expect(isUserLoggedIn(props)).toEqual(false);
    });

    it('returns false since the user is undefined', () => {
      const props = {
        user: undefined,
      };

      expect(isUserLoggedIn(props)).toEqual(false);
    });
  });
});

