const detection = require('../layout-type-detection');

describe('Layout Type Detection', () => {
  it('returns true when the layout is plus', () => {
    expect(detection.isPlus('plus')).toEqual(true);
  });

  it('returns false when the layout is lite', () => {
    expect(detection.isPlus('lite')).toEqual(false);
  });

  it('returns true when the layout is lite', () => {
    expect(detection.isLite('pluslite')).toEqual(true);
  });

  it('returns false when the layout is plus', () => {
    expect(detection.isLite('plus')).toEqual(false);
  });

  it('returns true when the layout is hidden', () => {
    expect(detection.isHidden('hidden')).toEqual(true);
  });

  it('returns false when the layout is plus', () => {
    expect(detection.isHidden('plus')).toEqual(false);
  });

  it('returns true when the layout is internal', () => {
    expect(detection.isInternal('internal')).toEqual(true);
  });

  it('returns false when the layout is plus', () => {
    expect(detection.isInternal('plus')).toEqual(false);
  });

  it('returns true when the layout is clean', () => {
    expect(detection.isClean('plusclean')).toEqual(true);
  });

  it('returns false when the layout is plus', () => {
    expect(detection.isClean('plus')).toEqual(false);
  });

  it('returns true when the layout is clean', () => {
    expect(detection.isAnyPlus('plusclean')).toEqual(true);
  });

  it('returns true when the layout is plus', () => {
    expect(detection.isAnyPlus('plus')).toEqual(true);
  });

  it('returns false when the layout is hidden', () => {
    expect(detection.isAnyPlus('hidden')).toEqual(false);
  });
});
