/* eslint-disable no-unused-vars */
const prepareToRender = require('../../prepare-to-render');

describe('Prepare to render', () => {
  it('returns the different results for the required services', (done) => {
    const itemsResponse = { items: [] };
    const categoriesResponse = { categories: [] };
    const services = [
      { key: 'items', execute: (req, res, params) => Promise.resolve(itemsResponse) },
      { key: 'categories', execute: (req, res, params) => Promise.resolve(categoriesResponse) },
    ];

    prepareToRender(services, {}, {}, {}).then((results) => {
      expect(results.items).toBeDefined();
      expect(results.items).toMatchObject(itemsResponse);
      done();
    });
  });
});
