const { isPlus: isFull } = require('@frontend-navigations/commons/layout-type-detection');
const types = require('./types');

const isHidden = type => type === types.HIDDEN;
const isLite = type => type === types.LITE;
const isInternal = type => type === types.INTERNAL;
const isClean = type => type === types.CLEAN;

module.exports = {
  isFull,
  isHidden,
  isLite,
  isInternal,
  isClean,
  types,
};
