const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Css = require('.');
const {
  FULL,
  INTERNAL,
} = require('../../constants/navigation-types');

const defaultProps = {
  platformId: 'TC',
  siteId: 'MLV',
  navigationType: FULL,
  firstRequestInlineStyles: false,
  browser: {
    name: 'chrome',
    major: '80',
  },
  cdn: {
    link: 'https://some-cdn-link.com',
  },
  configGetter: () => 'some-key-value',
  configEnvironment: 'some-environment',
  addConfigUpdateHandler: () => {},
};

describe('Classifieds Navigation', () => {
  describe('Css component', () => {
    it('should match snapshot with non-internal navigation type', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Css {...defaultProps} />
      );

      const renderedComponent = renderer.render(component);

      expect(renderedComponent).toMatchSnapshot();
    });

    it('should match snapshot with internal navigation type', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Css
          {...defaultProps}
          navigationType={INTERNAL}
        />
      );

      const renderedComponent = renderer.render(component);

      expect(renderedComponent).toMatchSnapshot();
    });

    it('should match snapshot with firstRequestInlineStyles configuration', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Css
          {...defaultProps}
          firstRequestInlineStyles
        />
      );

      const renderedComponent = renderer.render(component);

      expect(renderedComponent).toMatchSnapshot();
    });
  });
});
