const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const FooterLinks = require('.');

describe('Classified Navigation', () => {
  describe('Footer', () => {
    it('matches the default snapshot for Footer Links', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FooterLinks
          links={[
            {
              link: {
                href: 'https://www.mercadolibre.com.ar/loyalty',
              },
              name: 'Descuentos',
              id: 'loyalty',
            },
          ]}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Footer Links with an image', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FooterLinks
          links={[
            {
              link: {
                href: 'https://www.mercadolibre.com.ar/loyalty',
              },
              name: 'Descuentos',
              id: 'loyalty',
              icon: {
                image: 'https://image.jpeg',
                width: '123',
                height: '124',
              },
            },
          ]}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Footer Links with a link tag', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FooterLinks
          links={[
            {
              link: {
                href: 'https://www.mercadolibre.com.ar/loyalty',
              },
              name: 'Descuentos',
              id: 'loyalty',
            },
          ]}
          linkTags={{
            loyalty: {
              name: 'Nuevo',
            },
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
