const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const FooterLoggedInMenu = require('.');

describe('Classified Navigation', () => {
  describe('Footer Logged In User Menu', () => {
    it('matches the default snapshot for Footer Logged In User Menu', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FooterLoggedInMenu
          lowDensityPictureUrl="https://www.mercadolibre.com.ar/image.jpg"
          highDensityPictureUrl="https://www.mercadolibre.com.ar/big-image.jpg"
          hasPicture
          lazyLoad="off"
          username="username"
          logout={{
            link: '/logout',
            name: 'Salir',
          }}
          link="/my-account"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Footer Logged In User Menu with no image', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FooterLoggedInMenu
          hasPicture={false}
          lazyLoad="off"
          username="username"
          logout={{
            link: '/logout',
            name: 'Salir',
          }}
          link="/my-account"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
