const isUserLoggedIn = require('@frontend-navigations/commons/is-user-logged-in');
const guestUserMenuProps = require('./guest/props');
const loggedInUserMenuProps = require('./logged-in/props');

const userMenuProps = (props) => {
  const isLoggedIn = isUserLoggedIn(props);
  const baseUserMenuProps = {
    isUserLoggedIn: isLoggedIn,
  };

  if (isLoggedIn) {
    Object.assign(baseUserMenuProps, {
      loggedInMenu: loggedInUserMenuProps(props),
    });
  } else {
    Object.assign(baseUserMenuProps, {
      guestMenu: guestUserMenuProps(props),
    });
  }

  return baseUserMenuProps;
};

module.exports = userMenuProps;

