/* eslint-disable react/forbid-prop-types */
const React = require('react');
const PropTypes = require('prop-types');
const FooterAccess = require('../components/footer-access');
const FooterNavigation = require('../components/footer-navigation');
const BaseFooter = require('../components/base-footer');
const Brand = require('../components/brand');

const FooterDesktop = (props) => {
  const { footerAccess, footerNavigation, platformName, ...baseFooterProps } = props;
  return (
    <>
      <FooterAccess {...footerAccess} />
      <BaseFooter
        {...baseFooterProps}
      >
        <>
          <FooterNavigation {...footerNavigation} />
          <Brand platformName={platformName} />
        </>
      </BaseFooter>
    </>
  );
};

FooterDesktop.propTypes = {
  footerAccess: PropTypes.object,
  footerNavigation: PropTypes.object.isRequired,
};

FooterDesktop.defaultProps = {
  footerAccess: {},
};

module.exports = FooterDesktop;
