/* eslint-disable react/forbid-prop-types */
const React = require('react');
const PropTypes = require('prop-types');
const BaseFooter = require('../components/base-footer');
const UserMenu = require('../components/user-menu');
const FooterLinks = require('../components/footer-links');
const Brand = require('../components/brand');

const FooterMobile = (props) => {
  const {
    footerLinks,
    userMenu,
    platformName,
    footerNavigation,
    ...baseFooterProps
  } = props;

  return (
    <BaseFooter
      {...baseFooterProps}
      banner={<FooterLinks {...footerLinks} />}
      userMenuSection={<UserMenu {...userMenu} />}
      footerExhibitor={<Brand platformName={platformName} />}
    />
  );
};

FooterMobile.propTypes = {
  footerLinks: PropTypes.object.isRequired,
  userMenu: PropTypes.object.isRequired,
};

module.exports = FooterMobile;
