const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const BaseHeader = require('.');

const baseProps = {
  type: 'full',
  siteId: 'MLV',
  logo: {
    url: 'https://www.tucarro.com.ve',
    label: 'Classifieds',
  },
  melilab: {},
  userMenu: {
    isUserLoggedIn: false,
  },
  searchbox: {
    isDesktop: true,
  },
  navMenuSwitchId: 'navMenuSwitchId',
};

describe('Classifieds Navigation', () => {
  describe('Base Header Component', () => {
    it('matches the default snapshot for Mobile', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <BaseHeader
          {...baseProps}
        >
          <div />
        </BaseHeader>
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for Mobile with logo', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <BaseHeader
          {...baseProps}
          showLogo
        >
          <div />
        </BaseHeader>
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
