const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const InternalHeader = require('.');

describe('Classifieds Navigation', () => {
  describe('Internal Header Component', () => {
    it('matches the default snapshot for internal header component', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <InternalHeader
          title="Test"
          btnHref="/"
          btnText="back"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for internal header component with no back button', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <InternalHeader
          title="Test"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
