const React = require('react');
const PropTypes = require('prop-types');
const Melilab = require('../meli-lab');
const tabIndexes = require('../../../utils/tab-index');

const Logo = (props) => {
  const { url, label, melilab, searchFocus } = props;

  const logoComponents = [
    <a className="nav-logo" href={url} tabIndex={tabIndexes('logo', searchFocus)}>{label}</a>,
  ];

  if (melilab.isEnabled) {
    logoComponents.push(<Melilab {...melilab} />);
  }

  return logoComponents;
};

Logo.propTypes = {
  url: PropTypes.string.isRequired,
  melilab: PropTypes.shape({
    isEnabled: PropTypes.bool,
  }).isRequired,
  label: PropTypes.string.isRequired,
  searchFocus: PropTypes.bool,
};

Logo.defaultProps = {
  melilab: {
    isEnabled: false,
  },
  searchFocus: false,
};

module.exports = Logo;
