const React = require('react');
const PropTypes = require('prop-types');
const Category = require('./searchbox-category');
const SearchboxBase = require('./searchbox-base');

const SearchboxDesktop = ({ category, ...props }) => (
  <SearchboxBase {...props}>
    {category && <Category {...category} />}
  </SearchboxBase>
);

SearchboxDesktop.propTypes = {
  category: PropTypes.shape({
    id: PropTypes.string,
    name: PropTypes.string,
    defaultChecked: PropTypes.bool,
  }),
};

SearchboxDesktop.defaultProps = {
  category: null,
};
module.exports = SearchboxDesktop;
