const { getUserPictures } = require('@frontend-navigations/commons/user-pictures');

const avatarProps = (props) => {
  const { device, user, navigationPIIData } = props;

  const alt = navigationPIIData.preferredName || user.nickname;

  return {
    lazyLoad: device.desktop ? 'off' : 'on',
    ...getUserPictures(props),
    alt,
  };
};

module.exports = avatarProps;
