const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const FallbackMenu = require('.');

const sectionsLinks = [
  {
    link: {
      href: 'https://www.mercadolibre.com.ar',
    },
    name: 'Inicio',
    icon: 'nav-home-icon',
    selected: false,
    id: 'home',
  },
  {
    link: {
      href: 'https://www.mercadolibre.com.ar/ofertas',
    },
    name: 'Ofertas',
    icon: 'nav-deals-icon',
    selected: false,
    id: 'deals',
  },
];

describe('Classified Navigation', () => {
  describe('Fallback Menu Component', () => {
    it('matches the default snapshot for fallback user menu', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <FallbackMenu
          links={sectionsLinks}
        />
      );

      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
