const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Welcome = require('.');

describe('Classifieds Navigation', () => {
  describe('Welcome Component', () => {
    it('matches the default snapshot for welcome, in desktop for plus layout', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Welcome
          avatar={{
            alt: 'Test',
          }}
          type="plus"
          showAvatar
          nickname="Test"
          fallbackMenu={{
            links: [{
              link: {
                href: 'https://www.mercadolibre.com.ar',
              },
              name: 'Inicio',
              icon: 'nav-home-icon',
              selected: false,
              id: 'home',
            },
            {
              link: {
                href: 'https://www.mercadolibre.com.ar/ofertas',
              },
              name: 'Ofertas',
              icon: 'nav-deals-icon',
              selected: false,
              id: 'deals',
            }],
          }}
        />
      );

      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for welcome, in desktop for lite layout', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Welcome
          show
          avatar={{
            alt: 'Test',
          }}
          type="lite"
          nickname="Test"
          showAvatar
          userWidget={{
            baseUrl: 'https://ml.static.com',
            version: '1.0.0',
            domain: 'www.mercadolibre.com.ar',
            show: true,
          }}
          fallbackMenu={{
            links: [{
              link: {
                href: 'https://www.mercadolibre.com.ar',
              },
              name: 'Inicio',
              icon: 'nav-home-icon',
              selected: false,
              id: 'home',
            },
            {
              link: {
                href: 'https://www.mercadolibre.com.ar/ofertas',
              },
              name: 'Ofertas',
              icon: 'nav-deals-icon',
              selected: false,
              id: 'deals',
            }],
          }}
        />
      );

      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for welcome, in desktop for clean layout', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Welcome
          avatar={{
            alt: 'Test',
          }}
          show={false}
          type="plusclean"
          nickname="Test"
          fallbackMenu={{
            links: [{
              link: {
                href: 'https://www.mercadolibre.com.ar',
              },
              name: 'Inicio',
              icon: 'nav-home-icon',
              selected: false,
              id: 'home',
            },
            {
              link: {
                href: 'https://www.mercadolibre.com.ar/ofertas',
              },
              name: 'Ofertas',
              icon: 'nav-deals-icon',
              selected: false,
              id: 'deals',
            }],
          }}
        />
      );

      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
