const React = require('react');
const PropTypes = require('prop-types');
const LoggedIn = require('./logged-in');
const HamburguerMenu = require('../../hamburguer-menu');
const GuestMenu = require('./guest');

const namespace = 'nav-header-menu-mobile';
const MobileUserMenu = (props) => {
  const { isUserLoggedIn, navMenuSwitchId, guestMenu, loggedInMenu } = props;
  const navUserMobileClassName = isUserLoggedIn ? `${namespace}-logged` : `${namespace}-guest`;

  return (
    <>
      <HamburguerMenu navMenuSwitchId={navMenuSwitchId} />
      <nav id={namespace} className={navUserMobileClassName}>
        <div id={`${namespace}-content`}>
          { isUserLoggedIn ? <LoggedIn {...loggedInMenu} /> : <GuestMenu {...guestMenu} /> }
        </div>
      </nav>
    </>
  );
};

MobileUserMenu.propTypes = {
  navMenuSwitchId: PropTypes.string.isRequired,
  // eslint-disable-next-line react/forbid-prop-types
  guestMenu: PropTypes.object.isRequired,
  // eslint-disable-next-line react/forbid-prop-types
  loggedInMenu: PropTypes.object.isRequired,
  isUserLoggedIn: PropTypes.bool.isRequired,
};

module.exports = MobileUserMenu;
