const { getUserPictures } = require('@frontend-navigations/commons/user-pictures');

const avatarProps = (props, options = {}) => {
  const { urls, device, userInfo } = props;

  return {
    action: `https://www.${urls.mainDomain.link}/menu/user/picture`,
    lazyLoad: device.desktop ? 'off' : 'on',
    ...getUserPictures(props, options),
    actionLabel: !!userInfo && !!userInfo.picture ? userInfo.picture.action_label : '',
  };
};

module.exports = avatarProps;
