const React = require('react');
const PropTypes = require('prop-types');
const Avatar = require('./avatar');

const namespace = 'nav-header-menu-mobile';
const Welcome = (props) => {
  const { avatar, title, firstName, nickname, email } = props;

  return (
    <div id={`${namespace}-user-info`}>
      <Avatar {...avatar} />
      <div id="nav-header-user-greetings">{title || firstName || nickname}</div>
      <div id="nav-header-user-mail-or-level">
        <span>{email}</span>
      </div>
    </div>
  );
};

Welcome.propTypes = {
  // eslint-disable-next-line react/forbid-prop-types
  avatar: PropTypes.object.isRequired,
  title: PropTypes.string,
  firstName: PropTypes.string,
  email: PropTypes.string,
  nickname: PropTypes.string,
};

Welcome.defaultProps = {
  title: null,
  firstName: null,
  nickname: '',
  email: null,
};

module.exports = Welcome;
