const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const Header = require('.');

const baseProps = {
  type: 'full',
  siteId: 'MLV',
  logo: {
    url: 'https://www.tucarro.com.ve',
    label: 'Classifieds',
  },
  melilab: {},
  userMenu: {
    isUserLoggedIn: false,
  },
  searchbox: {
    isDesktop: true,
  },
  navMenuSwitchId: 'navMenuSwitchId',
};

describe('TuCarro Navigation', () => {
  describe('Full Component', () => {
    it('matches the default snapshot for full header', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Header
          {...baseProps}
          type="full"
          siteId="MLV"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('matches the default snapshot for full header in desktop', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <Header
          {...baseProps}
          type="full"
          siteId="MLV"
          isMobile={false}
          isDesktop
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
