const React = require('react');
const ShallowRenderer = require('react-test-renderer/shallow');
const HeaderInternal = require('.');

const baseProps = {
  type: 'full',
  siteId: 'MLV',
  logo: {
    url: 'https://www.tucarro.com.ve',
    label: 'Classifieds',
  },
  melilab: {},
  userMenu: {
    isUserLoggedIn: false,
  },
  searchbox: {
    isDesktop: true,
  },
  navMenuSwitchId: 'navMenuSwitchId',
};

describe('Classifieds Navigation', () => {
  describe('Header Internal', () => {
    it('returns the header in internal mode', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <HeaderInternal
          {...baseProps}
          type="internal"
          siteId="MLA"
          internal={{
            title: 'Internal',
            btnHref: '/',
          }}
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });

    it('returns the header in internal mode with no title and back href', () => {
      const renderer = new ShallowRenderer();
      const component = (
        <HeaderInternal
          {...baseProps}
          type="internal"
          siteId="MLA"
        />
      );
      expect(renderer.render(component)).toMatchSnapshot();
    });
  });
});
