// TODO: Change way we use tabindex, we should use a factory and not every component calculate tabindex number
const tabIndexes = {
  'skip-to-main-content': 1,
  logo: 2,
  'search-input': 3,
  'search-button': 4,
  exhibitor: 5,
  cp: 6,
  'nav-link': 7,
  'user-menu-link': 8,
  cart: 9,
};

const tabIndexesSearchFocus = {
  'skip-to-main-content': 4,
  logo: 1,
  'search-input': 2,
  'search-button': 3,
  exhibitor: 5,
  cp: 6,
  'nav-link': 7,
  'user-menu-link': 8,
  cart: 9,
};

// eslint-disable-next-line security/detect-object-injection
const get = (component, searchFocus = null) => (searchFocus ? tabIndexesSearchFocus[component] : tabIndexes[component]);

module.exports = get;
