const { getValue } = require('classified-navigation-config');
const {
  getLoginURL,
  getLogoutURL,
  getRegistrationURL,
} = require('@auth/urls');

const getUrlConfig = (req) => {
  const urlConfig = getValue({
    key: 'url',
    req,
    platform: req.platform,
  }) || {};

  return {
    ...urlConfig,
    login: {
      ...urlConfig.login,
      link: getLoginURL(req),
    },
    logout: {
      ...urlConfig.logout,
      link: getLogoutURL(req),
    },
    registration: {
      ...urlConfig.registration,
      link: getRegistrationURL(req),
    },
  };
};

module.exports = getUrlConfig;
